import React, { useEffect, useState } from "react";
import "./style.css";
import { MapContainer, TileLayer, useMap, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";
import schooldata from "../../export.json";
const icon = new Icon({
  iconUrl: "/school.png",
  iconSize: [40, 40],
});
const Home = () => {
  return (
    <MapContainer center={[27.7172, 85.324]} zoom={15} scrollWheelZoom={false}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {schooldata?.elements?.map((data, i) =>
        data?.lat && data?.lon && data?.tags?.name ? (
          <Marker key={i} position={[data?.lat, data?.lon]} icon={icon}>
            <Popup>
              <h1 style={{ fontSize: "14px" }}> {data?.tags?.name} </h1>
              <div className="school-details-container">
                <div className="school-details">
                  <p className="details-title">Opening Hour:</p>
                  <p className="details-data">{data?.tags?.opening_hours}</p>
                </div>
              </div>
            </Popup>
          </Marker>
        ) : null
      )}
    </MapContainer>
  );
};

export default Home;
