import React, { useEffect, useState } from "react";
import "./style.css";
import { MapContainer, TileLayer, useMap, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";
const icon = new Icon({
  iconUrl: "/graduate-hat-svgrepo-com.svg",
  iconSize: [20, 20],
});
const Home = () => {
  const [data, setData] = useState();

  useEffect(() => {
    fetch(
      `https://www.overpass-api.de/api/interpreter?data=[out:json];node[amenity=%22school%22](27.708018080548214,85.29195070266724,27.725057139346692,85.31458854675293);out%20meta;`
    )
      .then((res) => res.json())
      .then((res) => setData(res));
  });
  console.log(data);
  return (
    <MapContainer center={[27.7172, 85.324]} zoom={13} scrollWheelZoom={false}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {data?.elements?.map((data) => (
        <Marker position={[data?.lat, data?.lon]} icon={icon}>
          <Popup>{data?.tags?.name}</Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Home;
